mapper.select:
select setmeal.*,category.name categoryName from setmeal
join category on category.id=setmeal.category_id


        and setmeal.name like concat('%',#{name},'%')


        and setmeal.status=#{status}


        and setmeal.category_id=#{categoryId}


order by setmeal.create_time desc

mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime}, update_user = #{updateUser}
where id=#{id};

mapper.select:
select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
